unit.class:
public final class AcceptBluetoothService extends LifecycleService {
    private WalletApplication application;
    private WalletLiveData wallet;
    private WakeLock wakeLock;
    private AcceptBluetoothThread classicThread;
    private AcceptBluetoothThread paymentProtocolThread;

    private long serviceCreatedAt;

    private final Handler handler = new Handler();

    private static final long TIMEOUT_MS = 5 * DateUtils.MINUTE_IN_MILLIS;

    private static final Logger log = LoggerFactory.getLogger(AcceptBluetoothService.class);

    @Override
    public IBinder onBind(final Intent intent) {
        return null;
    }

    @Override
    public int onStartCommand(final Intent intent, final int flags, final int startId) {
        super.onStartCommand(intent, flags, startId);

        handler.removeCallbacks(timeoutRunnable);
        handler.postDelayed(timeoutRunnable, TIMEOUT_MS);

        return START_NOT_STICKY;
    }

    @Override
    public void onCreate() {
        serviceCreatedAt = System.currentTimeMillis();
        log.debug(".onCreate()");

        super.onCreate();
        this.application = (WalletApplication) getApplication();
        final BluetoothAdapter bluetoothAdapter = checkNotNull(BluetoothAdapter.getDefaultAdapter());
        final PowerManager pm = (PowerManager) getSystemService(Context.POWER_SERVICE);

        wakeLock = pm.newWakeLock(PowerManager.PARTIAL_WAKE_LOCK, getClass().getName());
        wakeLock.acquire();

        registerReceiver(bluetoothStateChangeReceiver, new IntentFilter(BluetoothAdapter.ACTION_STATE_CHANGED));

        try {
            classicThread = new AcceptBluetoothThread.ClassicBluetoothThread(bluetoothAdapter) {
                @Override
                public boolean handleTx(final Transaction tx) {
                    return AcceptBluetoothService.this.handleTx(tx);
                }
            };
            paymentProtocolThread = new AcceptBluetoothThread.PaymentProtocolThread(bluetoothAdapter) {
                @Override
                public boolean handleTx(final Transaction tx) {
                    return AcceptBluetoothService.this.handleTx(tx);
                }
            };
        } catch (final IOException x) {
            new Toast(this).longToast(R.string.error_bluetooth, x.getMessage());
            log.warn("problem with listening, stopping service", x);
            CrashReporter.saveBackgroundTrace(x, application.packageInfo());
            stopSelf();
        }

        wallet = new WalletLiveData(application);
        wallet.observe(this, new Observer<Wallet>() {
            @Override
            public void onChanged(final Wallet wallet) {
                classicThread.start();
                paymentProtocolThread.start();
            }
        });
    }

    private boolean handleTx(final Transaction tx) {
        log.info("tx " + tx.getHashAsString() + " arrived via blueooth");

        final Wallet wallet = this.wallet.getValue();
        try {
            if (wallet.isTransactionRelevant(tx)) {
                wallet.receivePending(tx, null);

                handler.post(new Runnable() {
                    @Override
                    public void run() {
                        BlockchainService.broadcastTransaction(AcceptBluetoothService.this, tx);
                    }
                });
            } else {
                log.info("tx " + tx.getHashAsString() + " irrelevant");
            }

            return true;
        } catch (final VerificationException x) {
            log.info("cannot verify tx " + tx.getHashAsString() + " received via bluetooth", x);
        }

        return false;
    }

    @Override
    public void onDestroy() {
        if (paymentProtocolThread != null)
            paymentProtocolThread.stopAccepting();
        if (classicThread != null)
            classicThread.stopAccepting();

        unregisterReceiver(bluetoothStateChangeReceiver);

        wakeLock.release();

        handler.removeCallbacksAndMessages(null);

        super.onDestroy();

        log.info("service was up for " + ((System.currentTimeMillis() - serviceCreatedAt) / 1000 / 60) + " minutes");
    }

    private final BroadcastReceiver bluetoothStateChangeReceiver = new BroadcastReceiver() {
        @Override
        public void onReceive(final Context context, final Intent intent) {
            final int state = intent.getIntExtra(BluetoothAdapter.EXTRA_STATE, 0);

            if (state == BluetoothAdapter.STATE_TURNING_OFF || state == BluetoothAdapter.STATE_OFF) {
                log.info("bluetooth was turned off, stopping service");

                stopSelf();
            }
        }
    };

    private final Runnable timeoutRunnable = new Runnable() {
        @Override
        public void run() {
            log.info("timeout expired, stopping service");

            stopSelf();
        }
    };
}

public abstract class AcceptBluetoothThread extends Thread {
    protected final BluetoothServerSocket listeningSocket;
    protected final AtomicBoolean running = new AtomicBoolean(true);

    protected static final Logger log = LoggerFactory.getLogger(AcceptBluetoothThread.class);

    private AcceptBluetoothThread(final BluetoothServerSocket listeningSocket) {
        this.listeningSocket = listeningSocket;
    }

    public static abstract class ClassicBluetoothThread extends AcceptBluetoothThread {
        public ClassicBluetoothThread(final BluetoothAdapter adapter) throws IOException {
            super(adapter.listenUsingInsecureRfcommWithServiceRecord(Bluetooth.CLASSIC_PAYMENT_PROTOCOL_NAME,
                    Bluetooth.CLASSIC_PAYMENT_PROTOCOL_UUID));
        }

        @Override
        public void run() {
            org.bitcoinj.core.Context.propagate(Constants.CONTEXT);

            while (running.get()) {
                try ( // start a blocking call, and return only on success or exception
                        final BluetoothSocket socket = listeningSocket.accept();
                        final DataInputStream is = new DataInputStream(socket.getInputStream());
                        final DataOutputStream os = new DataOutputStream(socket.getOutputStream())) {
                    log.info("accepted classic bluetooth connection");

                    boolean ack = true;

                    final int numMessages = is.readInt();

                    for (int i = 0; i < numMessages; i++) {
                        final int msgLength = is.readInt();
                        final byte[] msg = new byte[msgLength];
                        is.readFully(msg);

                        try {
                            final Transaction tx = new Transaction(Constants.NETWORK_PARAMETERS, msg);

                            if (!handleTx(tx))
                                ack = false;
                        } catch (final ProtocolException x) {
                            log.info("cannot decode message received via bluetooth", x);
                            ack = false;
                        }
                    }

                    os.writeBoolean(ack);
                } catch (final IOException x) {
                    log.info("exception in bluetooth accept loop", x);
                }
            }
        }
    }

    public static abstract class PaymentProtocolThread extends AcceptBluetoothThread {
        public PaymentProtocolThread(final BluetoothAdapter adapter) throws IOException {
            super(adapter.listenUsingInsecureRfcommWithServiceRecord(Bluetooth.BIP70_PAYMENT_PROTOCOL_NAME,
                    Bluetooth.BIP70_PAYMENT_PROTOCOL_UUID));
        }

        @Override
        public void run() {
            org.bitcoinj.core.Context.propagate(Constants.CONTEXT);

            while (running.get()) {
                try ( // start a blocking call, and return only on success or exception
                        final BluetoothSocket socket = listeningSocket.accept();
                        final DataInputStream is = new DataInputStream(socket.getInputStream());
                        final DataOutputStream os = new DataOutputStream(socket.getOutputStream())) {
                    log.info("accepted payment protocol bluetooth connection");

                    boolean ack = true;

                    final Protos.Payment payment = Protos.Payment.parseDelimitedFrom(is);

                    log.debug("got payment message");

                    for (final Transaction tx : PaymentProtocol
                            .parseTransactionsFromPaymentMessage(Constants.NETWORK_PARAMETERS, payment)) {
                        if (!handleTx(tx))
                            ack = false;
                    }

                    final String memo = ack ? "ack" : "nack";

                    log.info("sending {} via bluetooth", memo);

                    final PaymentACK paymentAck = PaymentProtocol.createPaymentAck(payment, memo);
                    paymentAck.writeDelimitedTo(os);
                } catch (final IOException x) {
                    log.info("exception in bluetooth accept loop", x);
                }
            }
        }
    }

    public void stopAccepting() {
        running.set(false);

        try {
            listeningSocket.close();
        } catch (final IOException x) {
            // swallow
        }
    }

    protected abstract boolean handleTx(Transaction tx);
}

