unit.class:
public class Display {


    public static View getRootView(Activity mActivity) {
        return mActivity.getWindow().getDecorView().findViewById(android.R.id.content);
    }


    @SuppressWarnings("deprecation")
    @SuppressLint("NewApi")
    public static Point getUsableSize(Context mContext) {
        Point displaySize = new Point();
        try {
            WindowManager manager = (WindowManager) mContext.getSystemService(Context.WINDOW_SERVICE);
            if (manager != null) {
                android.view.Display display = manager.getDefaultDisplay();
                if (display != null) {
					display.getSize(displaySize);
				}
            }
        } catch (Exception e) {
            Log.e(Constants.TAG , "Error checking display sizes", e);
        }
        return displaySize;
    }


    public static Point getVisibleSize(Activity activity) {
        Point displaySize = new Point();
        Rect r = new Rect();
		activity.getWindow().getDecorView().getWindowVisibleDisplayFrame(r);
        displaySize.x = r.right - r.left;
        displaySize.y = r.bottom - r.top;
        return displaySize;
    }


    public static Point getFullSize(View view) {
        Point displaySize = new Point();
        displaySize.x = view.getRootView().getWidth();
        displaySize.y = view.getRootView().getHeight();
        return displaySize;
    }


    public static int getStatusBarHeight(Context mContext) {
        int result = 0;
        int resourceId = mContext.getResources().getIdentifier("status_bar_height", "dimen", "android");
        if (resourceId > 0) {
            result = mContext.getResources().getDimensionPixelSize(resourceId);
        }
        return result;
    }


    public static int getNavigationBarHeightStandard(Context mContext) {
        int resourceId = mContext.getResources().getIdentifier("navigation_bar_height", "dimen", "android");
        if (resourceId > 0) {
            return mContext.getResources().getDimensionPixelSize(resourceId);
        }
        return 0;
    }


    public static int getNavigationBarHeight(View view) {
        return (getFullSize(view).y - getUsableSize(view.getContext()).y);
    }


    @SuppressLint("NewApi")
    public static int getActionbarHeight(Object mObject) {
        int res = 0;
		if (ActionBarActivity.class.isAssignableFrom(mObject.getClass())) {
			res = ((ActionBarActivity) mObject).getSupportActionBar().getHeight();
		} else if (Activity.class.isAssignableFrom(mObject.getClass())) {
            res = ((Activity) mObject).getActionBar().getHeight();
        }
        return res;
    }

//	public static int getActionBarHeight(Activity mActivity) {
//		Rect r = new Rect();
//		Window window = mActivity.getWindow();
//		window.getDecorView().getWindowVisibleDisplayFrame(r);
//		int StatusBarHeight = r.top;
//		int contentViewTop = window.findViewById(Window.ID_ANDROID_CONTENT).getTop();
//		int actionBarHeight = contentViewTop - StatusBarHeight;
//		return actionBarHeight;
//	}


    @TargetApi(Build.VERSION_CODES.JELLY_BEAN_MR1)
    public static Point getScreenDimensions(Context mContext) {
        WindowManager wm = (WindowManager) mContext.getSystemService(Context.WINDOW_SERVICE);
        android.view.Display display = wm.getDefaultDisplay();
        Point size = new Point();
        DisplayMetrics metrics = new DisplayMetrics();
        display.getRealMetrics(metrics);
        size.x = metrics.widthPixels;
        size.y = metrics.heightPixels;
        return size;
    }


    @TargetApi(Build.VERSION_CODES.JELLY_BEAN_MR1)
    public static int getNavigationBarHeightKitkat(Context mContext) {
        return getScreenDimensions(mContext).y - getUsableSize(mContext).y;
    }


	public static boolean orientationLandscape(Context context) {
		return context.getResources().getConfiguration().orientation == Configuration.ORIENTATION_LANDSCAPE;
	}

    public static int getSoftButtonsBarHeight(Activity activity) {
        // getRealMetrics is only available with API 17 and +
        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.JELLY_BEAN_MR1) {
            DisplayMetrics metrics = new DisplayMetrics();
            activity.getWindowManager().getDefaultDisplay().getMetrics(metrics);
            int usableHeight = metrics.heightPixels;
            activity.getWindowManager().getDefaultDisplay().getRealMetrics(metrics);
            int realHeight = metrics.heightPixels;
            if (realHeight > usableHeight)
                return realHeight - usableHeight;
            else
                return 0;
        }
        return 0;
    }

}

public class Fonts {

    /**
     * Overrides all the fonts set to TextView class descendants found in the
     * view passed as parameter
     */
    public static void overrideTextSize(Context context, SharedPreferences prefs, View v) {
        Context privateContext = context.getApplicationContext();
        try {
            if (v instanceof ViewGroup) {
                ViewGroup vg = (ViewGroup) v;
                for (int i = 0; i < vg.getChildCount(); i++) {
                    View child = vg.getChildAt(i);
                    overrideTextSize(privateContext, prefs, child);
                }
            } else if (v instanceof TextView) {
                float currentSize = DensityUtil.pxToDp(((TextView) v).getTextSize(), privateContext);
                int index = Arrays
                        .asList(privateContext.getResources().getStringArray(
                                R.array.text_size_values))
                        .indexOf(
                                prefs.getString("settings_text_size", "default"));
                float offset = privateContext.getResources().getIntArray(
                        R.array.text_size_offset)[index == -1 ? 0 : index];
                ((TextView) v).setTextSize(currentSize + offset);
            }
        } catch (Exception e) {
            Log.e(Constants.TAG, "Error setting font size", e);
        }
    }
}

public class IntentChecker {

	/**
	 * Retrieves
	 * @param ctx
	 * @param intent
	 * @return
	 */
	public static String resolveActivityPackage(Context ctx, Intent intent) {
		ComponentName activity= intent.resolveActivity(ctx.getPackageManager());
		return activity != null ? activity.getPackageName() : "";
	}

	/**
	 * Checks intent and features availability
	 *
	 * @param features
	 * @param ctx
	 * @param intent
	 * @return
	 */
	public static boolean isAvailable(Context ctx, Intent intent, String[] features) {
		boolean res = getCompatiblePackages(ctx, intent).size() > 0;

		if (features != null) {
			for (String feature : features) {
				res = res && ctx.getPackageManager().hasSystemFeature(feature);
			}
		}
		return res;
	}

	/**
	 * Checks Intent's action
	 *
	 * @param i      Intent to ckeck
	 * @param action Action to compare with
	 * @return
	 */
	public static boolean checkAction(Intent i, String action) {
		return action.equals(i.getAction());
	}


	/**
	 * Checks Intent's actions
	 *
	 * @param i      Intent to ckeck
	 * @param actions Multiple actions to compare with
	 * @return
	 */
	public static boolean checkAction(Intent i, String... actions) {
		for (String action : actions) {
			if (checkAction(i, action)) return true;
		}
		return false;
	}

	private static List<ResolveInfo> getCompatiblePackages(Context ctx, Intent intent) {
		final PackageManager mgr = ctx.getPackageManager();
		return mgr.queryIntentActivities(intent, PackageManager.MATCH_DEFAULT_ONLY);
	}
}

public class FileHelper {

    /**
     * Get a file path from a Uri. This will get the the path for Storage Access
     * Framework Documents, as well as the _data field for the MediaStore and
     * other file-based ContentProviders.
     *
     * @param context The context.
     * @param uri     The Uri to query.
     */
    @SuppressLint("NewApi")
    public static String getPath(final Context context, final Uri uri) {

        if (uri == null)
            return null;

        final boolean isKitKat = Build.VERSION.SDK_INT >= Build.VERSION_CODES.KITKAT;

        // DocumentProvider
        if (isKitKat && DocumentsContract.isDocumentUri(context, uri)) {
            // ExternalStorageProvider
            if (isExternalStorageDocument(uri)) {
                final String docId = DocumentsContract.getDocumentId(uri);
                final String[] split = docId.split(":");
                final String type = split[0];

                if ("primary".equalsIgnoreCase(type)) {
                    return Environment.getExternalStorageDirectory() + "/" + split[1];
                }

                // TODO handle non-primary volumes
            }
            // DownloadsProvider
            else if (isDownloadsDocument(uri)) {
				final Uri contentUri = ContentUris.withAppendedId(Uri.parse("content://downloads/public_downloads"),
						parseLong(DocumentsContract.getDocumentId(uri)));
                return getDataColumn(context, contentUri, null, null);
            }
            // MediaProvider
            else if (isMediaDocument(uri)) {
                final String docId = DocumentsContract.getDocumentId(uri);
                final String[] split = docId.split(":");
                final String type = split[0];
                MediaStoreFactory mediaStoreFactory = new MediaStoreFactory();
                Uri contentUri = mediaStoreFactory.createURI(type);

                final String selection = "_id=?";
                final String[] selectionArgs = new String[]{split[1]};

                return getDataColumn(context, contentUri, selection, selectionArgs);
            }
        }
        // MediaStore (and general)
        else if ("content".equalsIgnoreCase(uri.getScheme())) {
            return getDataColumn(context, uri, null, null);
        }
        // File
        else if ("file".equalsIgnoreCase(uri.getScheme())) {
            return uri.getPath();
        }

        return null;
    }


    /**
     * Get the value of the data column for this Uri. This is useful for
     * MediaStore Uris, and other file-based ContentProviders.
     *
     * @param context       The context.
     * @param uri           The Uri to query.
     * @param selection     (Optional) Filter used in the query.
     * @param selectionArgs (Optional) Selection arguments used in the query.
     * @return The value of the _data column, which is typically a file path.
     */
    public static String getDataColumn(Context context, Uri uri,
                                       String selection, String[] selectionArgs) {

        Cursor cursor = null;
        final String column = "_data";
        final String[] projection = {column};

        try {
            cursor = context.getContentResolver().query(uri, projection, selection, selectionArgs, null);
            if (cursor != null && cursor.moveToFirst()) {
                final int column_index = cursor.getColumnIndexOrThrow(column);
                return cursor.getString(column_index);
            }
        } catch (Exception e) {
            Log.e(Constants.TAG, "Error retrieving uri path", e);
        } finally {
            if (cursor != null)
                cursor.close();
        }
        return null;
    }


    /**
     * @param uri The Uri to check.
     * @return Whether the Uri authority is ExternalStorageProvider.
     */
    public static boolean isExternalStorageDocument(Uri uri) {
        return "com.android.externalstorage.documents".equals(uri.getAuthority());
    }


    /**
     * @param uri The Uri to check.
     * @return Whether the Uri authority is DownloadsProvider.
     */
    public static boolean isDownloadsDocument(Uri uri) {
        return "com.android.providers.downloads.documents".equals(uri.getAuthority());
    }


    /**
     * @param uri The Uri to check.
     * @return Whether the Uri authority is MediaProvider.
     */
    public static boolean isMediaDocument(Uri uri) {
        return "com.android.providers.media.documents".equals(uri.getAuthority());
    }


    public static InputStream getInputStream(Context mContext, Uri mUri) {
        InputStream inputStream;
        try {
            inputStream = mContext.getContentResolver().openInputStream(mUri);
            inputStream.close();
            return inputStream;
        } catch (IOException e) {
            return null;
        }
    }


    /**
     * Trying to retrieve file name from content resolver
     */
    public static String getNameFromUri(Context mContext, Uri uri) {
        String fileName = "";
        Cursor cursor = null;
        try {
            cursor = mContext.getContentResolver().query(uri, new String[]{"_display_name"}, null, null, null);
            if (cursor != null) {
                try {
                    if (cursor.moveToFirst()) {
                        fileName = cursor.getString(0);
                    }

                } catch (Exception e) {
                    Log.e(Constants.TAG, "Error managing diskk cache", e);
                }
            } else {
                fileName = uri.getLastPathSegment();
            }
        } catch (SecurityException e) {
            return null;
        } finally {
            if (cursor != null) {
                cursor.close();
            }
        }
        return fileName;
    }


    public static String getFilePrefix(File file) {
        return getFilePrefix(file.getName());
    }


    public static String getFilePrefix(String fileName) {
        String prefix = fileName;
        int index = fileName.indexOf(".");
        if (index != -1) {
            prefix = fileName.substring(0, index);
        }
        return prefix;
    }


    public static String getFileExtension(File file) {
        return getFileExtension(file.getName());
    }


    public static String getFileExtension(String fileName) {
        if (TextUtils.isEmpty(fileName)) return "";
        String extension = "";
        int index = fileName.lastIndexOf(".");
        if (index != -1) {
            extension = fileName.substring(index, fileName.length());
        }
        return extension;
    }
}

public class KeyboardUtils {

    public static void showKeyboard(View view) {
        if (view == null) {
            return;
        }

        view.requestFocus();

        InputMethodManager inputManager = (InputMethodManager) view.getContext().getSystemService(
                Context.INPUT_METHOD_SERVICE);
        inputManager.showSoftInput(view, InputMethodManager.SHOW_IMPLICIT);

        ((InputMethodManager) view.getContext().getSystemService(Context.INPUT_METHOD_SERVICE)).showSoftInput(view, 0);

        if (!isKeyboardShowed(view)) {
            inputManager.toggleSoftInput(InputMethodManager.SHOW_FORCED, 0);
        }
    }


    public static boolean isKeyboardShowed(View view) {
        if (view == null) {
            return false;
        }
        InputMethodManager inputManager = (InputMethodManager) view.getContext().getSystemService(
                Context.INPUT_METHOD_SERVICE);
        return inputManager.isActive(view);
    }


    public static void hideKeyboard(View view) {
        if (view == null) {
            return;
        }
        InputMethodManager imm = (InputMethodManager) view.getContext().getSystemService(Context.INPUT_METHOD_SERVICE);
        if (!imm.isActive()) {
            return;
        }
        imm.hideSoftInputFromWindow(view.getWindowToken(), 0);

//		if (!isKeyboardShowed(view)) {
//			imm.toggleSoftInput(InputMethodManager.HIDE_NOT_ALWAYS, InputMethodManager.RESULT_HIDDEN);
//		}

    }


    public static void hideKeyboard(MainActivity mActivity) {
        mActivity.getWindow().setSoftInputMode(
                WindowManager.LayoutParams.SOFT_INPUT_STATE_ALWAYS_HIDDEN);
    }
}

public class LoadingDialog extends ProgressDialog {

    Context context;


    public LoadingDialog(Context context) {
        super(context);
        this.context = context;
    }


    @Override
    public void onBackPressed() {
        super.onBackPressed();
        this.cancel();
    }

}

public class ParcelableUtil {

	public static byte[] marshall(Parcelable parceable) {
		Parcel parcel = Parcel.obtain();
		parceable.writeToParcel(parcel, 0);
		byte[] bytes = parcel.marshall();
		parcel.recycle();
		return bytes;
	}


	public static Parcel unmarshall(byte[] bytes) {
		Parcel parcel = Parcel.obtain();
		parcel.unmarshall(bytes, 0, bytes.length);
		parcel.setDataPosition(0); // This is extremely important!
		return parcel;
	}


	public static <T> T unmarshall(byte[] bytes, Parcelable.Creator<T> creator) {
		Parcel parcel = unmarshall(bytes);
		T result = creator.createFromParcel(parcel);
		parcel.recycle();
		return result;
	}



}

public class GeocodeHelper implements LocationListener {

	private static final String LOG_TAG = Constants.TAG;
	private static final String PLACES_API_BASE = "https://maps.googleapis.com/maps/api/place";
	private static final String TYPE_AUTOCOMPLETE = "/autocomplete";
	private static final String OUT_JSON = "/json";

	private static GeocodeHelper instance;
	private static LocationManager locationManager;


	private GeocodeHelper() {
		instance = this;
		locationManager = (LocationManager) OmniNotes.getAppContext().getSystemService(Context.LOCATION_SERVICE);
	}


	@Override
	public void onLocationChanged(Location newLocation) {
	}


	@Override
	public void onStatusChanged(String provider, int status, Bundle extras) {
	}


	@Override
	public void onProviderEnabled(String provider) {
	}


	@Override
	public void onProviderDisabled(String provider) {
	}


	public static void getLocation(OnGeoUtilResultListener onGeoUtilResultListener) {
		SmartLocation.LocationControl bod = SmartLocation.with(OmniNotes.getAppContext())
				.location(GeoCodeProviderFactory.getProvider(OmniNotes.getAppContext()))
				.config(LocationParams.NAVIGATION).oneFix();

		Observable<Location> locations = ObservableFactory.from(bod).timeout(2, TimeUnit.SECONDS);
		locations.subscribe(new Subscriber<Location>() {
			@Override
			public void onNext(Location location) {
				onGeoUtilResultListener.onLocationRetrieved(location);
				unsubscribe();
			}

			@Override
			public void onCompleted() {
			}

			@Override
			public void onError(Throwable e) {
				onGeoUtilResultListener.onLocationUnavailable();
				unsubscribe();
			}
		});
	}


	public static void stop() {
		SmartLocation.with(OmniNotes.getAppContext()).location().stop();
		if (Geocoder.isPresent()) {
			SmartLocation.with(OmniNotes.getAppContext()).geocoding().stop();
		}
	}


	static String getAddressFromCoordinates(Context mContext, double latitude,
												   double longitude) throws IOException {
		String addressString = "";
		Geocoder geocoder = new Geocoder(mContext, Locale.getDefault());
		List<Address> addresses = geocoder.getFromLocation(latitude, longitude, 1);
		if (addresses.size() > 0) {
			Address address = addresses.get(0);
			if (address != null) {
				addressString = address.getThoroughfare() + ", " + address.getLocality();
			}
		}
		return addressString;
	}


	public static void getAddressFromCoordinates(Location location,
												 final OnGeoUtilResultListener onGeoUtilResultListener) {
		if (!Geocoder.isPresent()) {
			onGeoUtilResultListener.onAddressResolved("");
		} else {
			SmartLocation.with(OmniNotes.getAppContext()).geocoding().reverse(location, (location1, list) -> {
				String address = list.size() > 0 ? list.get(0).getAddressLine(0) : null;
				onGeoUtilResultListener.onAddressResolved(address);
			});
		}
	}


	public static double[] getCoordinatesFromAddress(Context mContext, String address)
			throws IOException {
		double[] result = new double[2];
		Geocoder geocoder = new Geocoder(mContext, Locale.getDefault());
		List<Address> addresses = geocoder.getFromLocationName(address, 1);
		if (addresses.size() > 0) {
			double latitude = addresses.get(0).getLatitude();
			double longitude = addresses.get(0).getLongitude();
			result[0] = latitude;
			result[1] = longitude;
		}
		return result;
	}


	public static void getCoordinatesFromAddress(String address, final OnGeoUtilResultListener
			listener) {
		SmartLocation.with(OmniNotes.getAppContext()).geocoding().direct(address, (name, results) -> {
			if (results.size() > 0) {
				listener.onCoordinatesResolved(results.get(0).getLocation(), address);
			}
		});
	}


	public static List<String> autocomplete(String input) {
		String MAPS_API_KEY = BuildConfig.MAPS_API_KEY;
		if (TextUtils.isEmpty(MAPS_API_KEY)) {
			return Collections.emptyList();
		}
		ArrayList<String> resultList = null;

		HttpURLConnection conn = null;
		InputStreamReader in = null;
		StringBuilder jsonResults = new StringBuilder();
		try {
			URL url = new URL(PLACES_API_BASE + TYPE_AUTOCOMPLETE + OUT_JSON + "?key=" + MAPS_API_KEY + "&input=" +
					URLEncoder.encode(input, "utf8"));
			conn = (HttpURLConnection) url.openConnection();
			in = new InputStreamReader(conn.getInputStream());
			// Load the results into a StringBuilder
			int read;
			char[] buff = new char[1024];
			while ((read = in.read(buff)) != -1) {
				jsonResults.append(buff, 0, read);
			}
		} catch (MalformedURLException e) {
			Log.e(Constants.TAG, "Error processing Places API URL");
			return null;
		} catch (IOException e) {
			Log.e(Constants.TAG, "Error connecting to Places API");
			return null;
		} finally {
			if (conn != null) {
				conn.disconnect();
			}
			if (in != null) {
				try {
					in.close();
				} catch (IOException e) {
					Log.e(Constants.TAG, "Error closing address autocompletion InputStream");
				}
			}
		}

		try {
			// Create a JSON object hierarchy from the results
			JSONObject jsonObj = new JSONObject(jsonResults.toString());
			JSONArray predsJsonArray = jsonObj.getJSONArray("predictions");
			// Extract the Place descriptions from the results
			resultList = new ArrayList<>(predsJsonArray.length());
			for (int i = 0; i < predsJsonArray.length(); i++) {
				resultList.add(predsJsonArray.getJSONObject(i).getString("description"));
			}
		} catch (JSONException e) {
			Log.e(Constants.TAG, "Cannot process JSON results", e);
		} finally {
			if (conn != null) {
				conn.disconnect();
			}
			SystemHelper.closeCloseable(in);
		}
		return resultList;
	}


	public static boolean areCoordinates(String string) {
		Pattern p = Pattern.compile("^[-+]?([1-8]?\\d(\\.\\d+)?|90(\\.0+)?),\\s*[-+]?(180(\\.0+)?|((1[0-7]\\d)|" +
				"([1-9]?\\d))(\\.\\d+)?)$");
		Matcher m = p.matcher(string);
		return m.matches();
	}
}

public class Navigation {

    public static final int NOTES = 0;
    public static final int ARCHIVE = 1;
    public static final int REMINDERS = 2;
    public static final int TRASH = 3;
    public static final int UNCATEGORIZED = 4;
    public static final int CATEGORY = 5;


    /**
     * Returns actual navigation status
     */
    public static int getNavigation() {
        String[] navigationListCodes = OmniNotes.getAppContext().getResources().getStringArray(R.array.navigation_list_codes);
        String navigation = getNavigationText();

        if (navigationListCodes[NOTES].equals(navigation)) {
            return NOTES;
        } else if (navigationListCodes[ARCHIVE].equals(navigation)) {
            return ARCHIVE;
        } else if (navigationListCodes[REMINDERS].equals(navigation)) {
            return REMINDERS;
        } else if (navigationListCodes[TRASH].equals(navigation)) {
            return TRASH;
        } else if (navigationListCodes[UNCATEGORIZED].equals(navigation)) {
            return UNCATEGORIZED;
        } else {
            return CATEGORY;
        }
    }


    public static String getNavigationText() {
        Context mContext = OmniNotes.getAppContext();
        String[] navigationListCodes = mContext.getResources().getStringArray(R.array.navigation_list_codes);
        @SuppressWarnings("static-access")
        String navigation = mContext.getSharedPreferences(Constants.PREFS_NAME,
                mContext.MODE_MULTI_PROCESS).getString(Constants.PREF_NAVIGATION, navigationListCodes[0]);
        return navigation;
    }


    /**
     * Retrieves category currently shown
     *
     * @return id of category or null if current navigation is not a category
     */
    public static Long getCategory() {
        if (getNavigation() == CATEGORY) {
			return Long.valueOf(OmniNotes.getAppContext().getSharedPreferences(Constants.PREFS_NAME, Context
					.MODE_MULTI_PROCESS).getString(Constants.PREF_NAVIGATION, ""));
		} else {
            return null;
        }
    }


    /**
     * Checks if passed parameters is the actual navigation status
     */
    public static boolean checkNavigation(int navigationToCheck) {
        return checkNavigation(new Integer[]{navigationToCheck});
    }


    public static boolean checkNavigation(Integer[] navigationsToCheck) {
        boolean res = false;
        int navigation = getNavigation();
        for (int navigationToCheck : new ArrayList<>(Arrays.asList(navigationsToCheck))) {
            if (navigation == navigationToCheck) {
                res = true;
                break;
            }
        }
        return res;
    }


    /**
     * Checks if passed parameters is the category user is actually navigating in
     */
    public static boolean checkNavigationCategory(Category categoryToCheck) {
        Context mContext = OmniNotes.getAppContext();
        String[] navigationListCodes = mContext.getResources().getStringArray(R.array.navigation_list_codes);
        String navigation = mContext.getSharedPreferences(Constants.PREFS_NAME, Context.MODE_MULTI_PROCESS).getString(Constants.PREF_NAVIGATION, navigationListCodes[0]);
        return (categoryToCheck != null && navigation.equals(String.valueOf(categoryToCheck.getId())));
    }

}

