	}
}

public class NotificationsHelper {

    private Context mContext;
    private Builder mBuilder;
    private NotificationManager mNotificationManager;


    public NotificationsHelper(Context mContext) {
        this.mContext = mContext.getApplicationContext();
    }


    /**
     * Creation of notification on operations completed
     */
    public NotificationsHelper createNotification(int smallIcon, String title, PendingIntent notifyIntent) {
        mBuilder = new NotificationCompat.Builder(mContext).setSmallIcon(smallIcon).setContentTitle(title)
                .setAutoCancel(true).setColor(mContext.getResources().getColor(R.color.colorAccent));
        mBuilder.setContentIntent(notifyIntent);
        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.LOLLIPOP) {
            setLargeIcon(R.drawable.logo_notification_lollipop);
        } else {
            setLargeIcon(R.mipmap.ic_launcher);
        }

        return this;
    }


    public Builder getBuilder() {
        return mBuilder;
    }


    public NotificationsHelper setLargeIcon(Bitmap largeIconBitmap) {
        mBuilder.setLargeIcon(largeIconBitmap);
        return this;
    }


    public NotificationsHelper setLargeIcon(int largeIconResource) {
        Bitmap largeIconBitmap = BitmapFactory.decodeResource(mContext.getResources(), largeIconResource);
        return setLargeIcon(largeIconBitmap);
    }


    public NotificationsHelper setRingtone(String ringtone) {
        // Ringtone options
        if (ringtone != null) {
            mBuilder.setSound(Uri.parse(ringtone));
        }
        return this;
    }


    public NotificationsHelper setVibration() {
        return setVibration(null);
    }


    public NotificationsHelper setVibration(long[] pattern) {
        if (pattern == null || pattern.length == 0) {
            pattern = new long[]{500, 500};
        }
        mBuilder.setVibrate(pattern);
        return this;
    }


    public NotificationsHelper setLedActive() {
        mBuilder.setLights(Color.BLUE, 1000, 1000);
        return this;
    }


    public NotificationsHelper setIcon(int icon) {
        mBuilder.setSmallIcon(icon);
        return this;
    }


    public NotificationsHelper setMessage(String message) {
        mBuilder.setContentText(message);
        return this;
    }


    public NotificationsHelper setIndeterminate() {
        mBuilder.setProgress(0, 0, true);
        return this;
    }


    public NotificationsHelper setOngoing() {
        mBuilder.setOngoing(true);
        return this;
    }


    public NotificationsHelper show() {
        show(0);
        return this;
    }


    public NotificationsHelper show(long id) {
        if (mNotificationManager == null) {
            mNotificationManager = (NotificationManager) mContext.getSystemService(Context.NOTIFICATION_SERVICE);
        }
        Notification mNotification = mBuilder.build();
        if (mNotification.contentIntent == null) {
            // Creates a dummy PendingIntent
            mBuilder.setContentIntent(PendingIntent.getActivity(mContext, 0, new Intent(),
                    PendingIntent.FLAG_UPDATE_CURRENT));
        }
        // Builds an anonymous Notification object from the builder, and passes it to the NotificationManager
        mNotificationManager.notify(String.valueOf(id), 0, mBuilder.build());
        return this;
    }

}

public class ReminderHelper {

	public static void addReminder(Context context, Note note) {
		if (note.getAlarm() != null) {
			addReminder(context, note, Long.parseLong(note.getAlarm()));
		}
	}


	public static void addReminder(Context context, Note note, long reminder) {
		if (DateUtils.isFuture(reminder)) {
			Intent intent = new Intent(context, AlarmReceiver.class);
			intent.putExtra(Constants.INTENT_NOTE, ParcelableUtil.marshall(note));
			PendingIntent sender = PendingIntent.getBroadcast(context, getRequestCode(note), intent,
					PendingIntent.FLAG_CANCEL_CURRENT);
			AlarmManager am = (AlarmManager) context.getSystemService(Context.ALARM_SERVICE);
			if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.KITKAT) {
				am.setExact(AlarmManager.RTC_WAKEUP, reminder, sender);
			} else {
				am.set(AlarmManager.RTC_WAKEUP, reminder, sender);
			}
		}
	}


	/**
	 * Checks if exists any reminder for given note
	 */
	public static boolean checkReminder(Context context, Note note) {
		return PendingIntent.getBroadcast(context, getRequestCode(note), new Intent(context, AlarmReceiver
				.class), PendingIntent.FLAG_NO_CREATE) != null;
	}


	static int getRequestCode(Note note) {
		Long longCode = note.getCreation() != null ? note.getCreation() : Calendar.getInstance().getTimeInMillis() / 1000L;
		return longCode.intValue();
	}


	public static void removeReminder(Context context, Note note) {
		if (!TextUtils.isEmpty(note.getAlarm())) {
			AlarmManager am = (AlarmManager) context.getSystemService(Context.ALARM_SERVICE);
			Intent intent = new Intent(context, AlarmReceiver.class);
			PendingIntent p = PendingIntent.getBroadcast(context, getRequestCode(note), intent, 0);
			am.cancel(p);
			p.cancel();
		}
	}


	public static void showReminderMessage(String reminderString) {
		if (reminderString != null) {
			long reminder = Long.parseLong(reminderString);
			if (reminder > Calendar.getInstance().getTimeInMillis()) {
				new Handler(OmniNotes.getAppContext().getMainLooper()).post(() -> Toast.makeText(OmniNotes
								.getAppContext(),
						OmniNotes.getAppContext().getString(R.string.alarm_set_on) + " " + DateHelper.getDateTimeShort
								(OmniNotes.getAppContext(), reminder), Toast.LENGTH_LONG).show());
			}
		}
	}
}

public class Security {


    public static String md5(String s) {
        try {
            // Create MD5 Hash
            MessageDigest digest = java.security.MessageDigest.getInstance("MD5");
            digest.update(s.getBytes());
            byte messageDigest[] = digest.digest();

            // Create Hex String
            StringBuilder hexString = new StringBuilder();
            for (int i = 0; i < messageDigest.length; i++)
                hexString.append(Integer.toHexString(0xFF & messageDigest[i]));
            return hexString.toString();

        } catch (NoSuchAlgorithmException e) {
            Log.d(Constants.TAG, "Something is gone wrong calculating MD5", e);
        }
        return "";
    }


    public static String encrypt(String value, String password) {
        String encrypedValue = "";
        try {
            DESKeySpec keySpec = new DESKeySpec(password.getBytes("UTF8"));
            SecretKeyFactory keyFactory = SecretKeyFactory.getInstance("DES");
            SecretKey key = keyFactory.generateSecret(keySpec);
            byte[] clearText = value.getBytes("UTF8");
            // Cipher is not thread safe
            Cipher cipher = Cipher.getInstance("DES");
            cipher.init(Cipher.ENCRYPT_MODE, key);
            encrypedValue = Base64.encodeToString(cipher.doFinal(clearText), Base64.DEFAULT);
            return encrypedValue;
		} catch (InvalidKeyException | NoSuchPaddingException | InvalidKeySpecException | BadPaddingException |
				IllegalBlockSizeException | NoSuchAlgorithmException | UnsupportedEncodingException e) {
			Log.d(Constants.TAG, "Something is gone wrong encrypting", e);
        }
        return encrypedValue;
    }


    public static String decrypt(String value, String password) {
        String decryptedValue;
        try {
            DESKeySpec keySpec = new DESKeySpec(password.getBytes("UTF8"));
            SecretKeyFactory keyFactory = SecretKeyFactory.getInstance("DES");
            SecretKey key = keyFactory.generateSecret(keySpec);

            byte[] encrypedPwdBytes = Base64.decode(value, Base64.DEFAULT);
            // cipher is not thread safe
            Cipher cipher = Cipher.getInstance("DES");
            cipher.init(Cipher.DECRYPT_MODE, key);
            byte[] decrypedValueBytes = (cipher.doFinal(encrypedPwdBytes));

            decryptedValue = new String(decrypedValueBytes);
		} catch (InvalidKeyException | UnsupportedEncodingException | InvalidKeySpecException |
				NoSuchAlgorithmException | BadPaddingException | NoSuchPaddingException | IllegalBlockSizeException
				e) {
			Log.e(Constants.TAG, "Error decrypting");
            return value;
            // try-catch ensure compatibility with old masked (without encryption) values
        } catch (IllegalArgumentException e) {
            Log.e(Constants.TAG, "Error decrypting: old notes were not encrypted but just masked to users");
            return value;
        }
        return decryptedValue;
    }


}

public class ShortcutHelper {


    /**
     * Adding shortcut on Home screen
     */
    public static void addShortcut(Context context, Note note) {
        Intent shortcutIntent = new Intent(context, MainActivity.class);
        shortcutIntent.putExtra(Constants.INTENT_KEY, note.get_id());
        shortcutIntent.setAction(Constants.ACTION_SHORTCUT);

        Intent addIntent = new Intent();
        addIntent.putExtra(Intent.EXTRA_SHORTCUT_INTENT, shortcutIntent);
        String shortcutTitle = note.getTitle().length() > 0 ? note.getTitle() : DateHelper.getFormattedDate(note
				.getCreation(), OmniNotes.getSharedPreferences().getBoolean(Constants
				.PREF_PRETTIFIED_DATES, true));
        addIntent.putExtra(Intent.EXTRA_SHORTCUT_NAME, shortcutTitle);
        addIntent.putExtra(Intent.EXTRA_SHORTCUT_ICON_RESOURCE,
                Intent.ShortcutIconResource.fromContext(context, R.drawable.ic_shortcut));
        addIntent.setAction("com.android.launcher.action.INSTALL_SHORTCUT");

        context.sendBroadcast(addIntent);
    }

    /**
     * Removes note shortcut from home launcher
     */
    public static void removeshortCut(Context context, Note note) {
        Intent shortcutIntent = new Intent(context, MainActivity.class);
        shortcutIntent.putExtra(Constants.INTENT_KEY, note.get_id());
        shortcutIntent.setAction(Constants.ACTION_SHORTCUT);

        Intent addIntent = new Intent();
        addIntent.putExtra(Intent.EXTRA_SHORTCUT_INTENT, shortcutIntent);
		String shortcutTitle = note.getTitle().length() > 0 ? note.getTitle() : DateHelper.getFormattedDate(note
				.getCreation(), OmniNotes.getSharedPreferences().getBoolean(Constants
				.PREF_PRETTIFIED_DATES, true));

        addIntent.putExtra(Intent.EXTRA_SHORTCUT_NAME, shortcutTitle);

        addIntent.setAction("com.android.launcher.action.UNINSTALL_SHORTCUT");
        context.sendBroadcast(addIntent);
    }
}

public class PasswordHelper {


    public static void requestPassword(final Activity mActivity, final PasswordValidator mPasswordValidator) {
        LayoutInflater inflater = mActivity.getLayoutInflater();
        final View v = inflater.inflate(R.layout.password_request_dialog_layout, null);
        final EditText passwordEditText = (EditText) v.findViewById(R.id.password_request);

        MaterialDialog dialog = new MaterialDialog.Builder(mActivity)
                .autoDismiss(false)
                .title(R.string.insert_security_password)
                .customView(v, false)
                .positiveText(R.string.ok)
                .positiveColorRes(R.color.colorPrimary)
                .onPositive((dialog12, which) -> {
                    // When positive button is pressed password correctness is checked
                    String oldPassword = mActivity.getSharedPreferences(Constants.PREFS_NAME, Context
                            .MODE_MULTI_PROCESS)
                            .getString(Constants.PREF_PASSWORD, "");
                    String password = passwordEditText.getText().toString();
                    // The check is done on password's hash stored in preferences
                    boolean result = Security.md5(password).equals(oldPassword);

                    // In case password is ok dialog is dismissed and result sent to callback
                    if (result) {
                        KeyboardUtils.hideKeyboard(passwordEditText);
                        dialog12.dismiss();
                        mPasswordValidator.onPasswordValidated(PasswordValidator.Result.SUCCEED);
                        // If password is wrong the auth flow is not interrupted and simply a message is shown
                    } else {
                        passwordEditText.setError(mActivity.getString(R.string.wrong_password));
                    }
                })
                .neutralText(mActivity.getResources().getString(R.string.password_forgot))
                .onNeutral((dialog13, which) -> {
                    PasswordHelper.resetPassword(mActivity);
                    mPasswordValidator.onPasswordValidated(PasswordValidator.Result.RESTORE);
                    dialog13.dismiss();
                })
                .build();

        dialog.setOnCancelListener(dialog1 -> {
            KeyboardUtils.hideKeyboard(passwordEditText);
            dialog1.dismiss();
            mPasswordValidator.onPasswordValidated(PasswordValidator.Result.FAIL);
        });

        passwordEditText.setOnEditorActionListener((textView, actionId, keyEvent) -> {
            if (actionId == EditorInfo.IME_ACTION_DONE) {
                dialog.getActionButton(DialogAction.POSITIVE).callOnClick();
                return true;
            }
            return false;
        });

        dialog.show();

        new Handler().postDelayed(() -> KeyboardUtils.showKeyboard(passwordEditText), 100);
    }


    public static void resetPassword(final Activity mActivity) {
        View layout = mActivity.getLayoutInflater().inflate(R.layout.password_reset_dialog_layout, null);
        final EditText answerEditText = (EditText) layout.findViewById(R.id.reset_password_answer);

        MaterialDialog dialog = new MaterialDialog.Builder(mActivity)
                .title(OmniNotes.getSharedPreferences().getString(Constants.PREF_PASSWORD_QUESTION, ""))
                .customView(layout, false)
                .autoDismiss(false)
                .contentColorRes(R.color.text_color)
                .positiveText(R.string.ok)
                .onPositive((dialogElement, which) -> {
                    // When positive button is pressed answer correctness is checked
                    String oldAnswer = OmniNotes.getSharedPreferences().getString(Constants.PREF_PASSWORD_ANSWER, "");
                    String answer1 = answerEditText.getText().toString();
                    // The check is done on password's hash stored in preferences
                    boolean result = Security.md5(answer1).equals(oldAnswer);
                    if (result) {
                        dialogElement.dismiss();
                        removePassword();
                    } else {
                        answerEditText.setError(mActivity.getString(R.string.wrong_answer));
                    }
                }).build();
        dialog.show();

        answerEditText.setOnEditorActionListener((textView, actionId, keyEvent) -> {
            if (actionId == EditorInfo.IME_ACTION_DONE) {
                dialog.getActionButton(DialogAction.POSITIVE).callOnClick();
                return true;
            }
            return false;
        });

        new Handler().postDelayed(() -> KeyboardUtils.showKeyboard(answerEditText), 100);
    }


    public static void removePassword() {
        Observable
                .from(DbHelper.getInstance().getNotesWithLock(true))
                .subscribeOn(Schedulers.newThread())
                .observeOn(AndroidSchedulers.mainThread())
                .doOnNext(note -> {
                    note.setLocked(false);
                    DbHelper.getInstance().updateNote(note, false);
                })
                .doOnCompleted(() -> {
                    EventBus.getDefault().post(new PasswordRemovedEvent());
                    OmniNotes.getSharedPreferences().edit()
                            .remove(Constants.PREF_PASSWORD)
                            .remove(Constants.PREF_PASSWORD_QUESTION)
                            .remove(Constants.PREF_PASSWORD_ANSWER)
                            .remove("settings_password_access")
                            .apply();
                })
                .subscribe();
    }
}

