		System.exit(0);
	}


	/**
	 * Performs closure of multiple closeables objects
	 *
	 * @param closeables Objects to close
	 */
	public static void closeCloseable(Closeable... closeables) {
		for (Closeable closeable : closeables) {
			if (closeable != null) {
				try {
					closeable.close();
				} catch (IOException e) {
					Log.w(Constants.TAG, "Can't close " + closeable, e);
				}
			}
		}
	}
}

public class DatePickerDialogFragment extends DialogFragment {

    public static final String DEFAULT_DATE = "default_date";

    private OnDateSetListener mListener;
    private Long defaultDate;


    @Override
    public void onAttach(Activity activity) {
        super.onAttach(activity);
        if (getArguments().containsKey(DEFAULT_DATE)) {
            this.defaultDate = getArguments().getLong(DEFAULT_DATE);
        }

        try {
            mListener = (OnDateSetListener) activity;
        } catch (ClassCastException e) {
            throw new ClassCastException(activity.toString()
                    + " must implement OnDateSetListener");
        }
    }


    @Override
    public void onDetach() {
        this.mListener = null;
        super.onDetach();
    }


    @NonNull
    @Override
    public Dialog onCreateDialog(Bundle savedInstanceState) {
        // Use the current date as the default date in the picker
        Calendar cal = DateUtils.getCalendar(defaultDate);
        int y = cal.get(Calendar.YEAR);
        int m = cal.get(Calendar.MONTH);
        int d = cal.get(Calendar.DAY_OF_MONTH);

        // Jelly Bean introduced a bug in DatePickerDialog (and possibly 
        // TimePickerDialog as well), and one of the possible solutions is 
        // to postpone the creation of both the listener and the BUTTON_* .
        // 
        // Passing a null here won't harm because DatePickerDialog checks for a null
        // whenever it reads the listener that was passed here. >>> This seems to be 
        // true down to 1.5 / API 3, up to 4.1.1 / API 16. <<< No worries. For now.
        //
        // See my own question and answer, and details I included for the issue:
        //
        // http://stackoverflow.com/a/11493752/489607
        // http://code.google.com/p/android/issues/detail?id=34833
        //
        // Of course, suggestions welcome.

        final DatePickerDialog picker = new DatePickerDialog(getActivity(), DatePickerDialog.THEME_HOLO_LIGHT,
                mListener, y, m, d);
        picker.setTitle("");

		picker.setButton(DialogInterface.BUTTON_POSITIVE,
				getActivity().getString(android.R.string.ok),
				(dialog, which) -> {
					DatePicker dp = picker.getDatePicker();
					mListener.onDateSet(dp,
							dp.getYear(), dp.getMonth(), dp.getDayOfMonth());
				});
		picker.setButton(DialogInterface.BUTTON_NEGATIVE,
				getActivity().getString(android.R.string.cancel),
				(dialog, which) -> {});

        return picker;
    }

}

public class TagsHelper {


    public static List<Tag> getAllTags() {
        return DbHelper.getInstance().getTags();
    }


	public static HashMap<String, Integer> retrieveTags(Note note) {
		HashMap<String, Integer> tagsMap = new HashMap<>();
		for (String token : (note.getTitle() + " " + note.getContent()).replaceAll("\n", " ").trim().split(" ")) {
			if (RegexPatternsConstants.HASH_TAG.matcher(token).matches()) {
				int count = tagsMap.get(token) == null ? 0 : tagsMap.get(token);
				tagsMap.put(token, ++count);
			}
		}
		return tagsMap;
	}


    public static Pair<String, List<Tag>> addTagToNote(List<Tag> tags, Integer[] selectedTags, Note note) {
        StringBuilder sbTags = new StringBuilder();
        List<Tag> tagsToRemove = new ArrayList<>();
        HashMap<String, Integer> tagsMap = retrieveTags(note);

        List<Integer> selectedTagsList = Arrays.asList(selectedTags);
        for (int i = 0; i < tags.size(); i++) {
            if (mapContainsTag(tagsMap, tags.get(i))) {
                if (!selectedTagsList.contains(i)) {
                    tagsToRemove.add(tags.get(i));
                }
            } else {
                if (selectedTagsList.contains(i)) {
                    if (sbTags.length() > 0) {
                        sbTags.append(" ");
                    }
                    sbTags.append(tags.get(i));
                }
            }
        }
        return Pair.create(sbTags.toString(), tagsToRemove);
    }


    private static boolean mapContainsTag(HashMap<String, Integer> tagsMap, Tag tag) {
        for (String tagsMapItem : tagsMap.keySet()) {
            if (tagsMapItem.equals(tag.getText())) {
                return true;
            }
        }
        return false;
    }


    public static Pair<String, String> removeTag(String noteTitle, String noteContent, List<Tag> tagsToRemove) {
        String title = noteTitle, content = noteContent;
        for (Tag tagToRemove : tagsToRemove) {
            title = title.replaceAll(tagToRemove.getText(), "");
            content = content.replaceAll(tagToRemove.getText(), "");
        }
        return new Pair<>(title, content);
    }


    public static String[] getTagsArray(List<Tag> tags) {
        String[] tagsArray = new String[tags.size()];
        for (int i = 0; i < tags.size(); i++) {
            tagsArray[i] = tags.get(i).getText().substring(1) + " (" + tags.get(i).getCount() + ")";
        }
        return tagsArray;
    }


    public static Integer[] getPreselectedTagsArray(Note note, List<Tag> tags) {
        List<Note> notes = new ArrayList<>();
        notes.add(note);
        return getPreselectedTagsArray(notes, tags);
    }


    public static Integer[] getPreselectedTagsArray(List<Note> notes, List<Tag> tags) {
        final Integer[] preSelectedTags;
        if (notes.size() == 1) {
            List<Integer> t = new ArrayList<>();
            for (String noteTag : TagsHelper.retrieveTags(notes.get(0)).keySet()) {
                for (Tag tag : tags) {
                    if (tag.getText().equals(noteTag)) {
                        t.add(tags.indexOf(tag));
                        break;
                    }
                }
            }
            preSelectedTags = t.toArray(new Integer[t.size()]);
        } else {
            preSelectedTags = new Integer[]{};
        }
        return preSelectedTags;
    }
}

public class TextHelper {

    /**
     * @param note
     * @return
     */
    public static Spanned[] parseTitleAndContent(Context mContext, Note note) {

        final int CONTENT_SUBSTRING_LENGTH = 300;

		String titleText = note.getTitle();
		String contentText = limit(note.getContent().trim(), CONTENT_SUBSTRING_LENGTH, false, true);

        // Masking title and content string if note is locked
        if (note.isLocked()
                && !mContext.getSharedPreferences(Constants.PREFS_NAME, Context.MODE_MULTI_PROCESS).getBoolean(
                "settings_password_access", false)) {
            // This checks if a part of content is used as title and should be partially masked
            if (!note.getTitle().equals(titleText) && titleText.length() > 3) {
                titleText = limit(titleText, 4, false, false);
            }
            contentText = "";
        }

        // Replacing checkmarks symbols with html entities
        Spanned contentSpanned;
        if (note.isChecklist() && !TextUtils.isEmpty(contentText)) {
			contentSpanned = Html.fromHtml(contentText
                    .replace(it.feio.android.checklistview.interfaces.Constants.CHECKED_SYM,
							it.feio.android.checklistview.interfaces.Constants.CHECKED_ENTITY)
                    .replace(it.feio.android.checklistview.interfaces.Constants.UNCHECKED_SYM,
							it.feio.android.checklistview.interfaces.Constants.UNCHECKED_ENTITY)
                    .replace(System.getProperty("line.separator"), "<br/>"));
        } else {
            contentSpanned = new SpannedString(contentText);
        }

        return new Spanned[]{new SpannedString(titleText), contentSpanned};
    }


    private static String limit(String value, int length, boolean singleLine, boolean elipsize) {
        StringBuilder buf = new StringBuilder(value);
        int indexNewLine = buf.indexOf(System.getProperty("line.separator"));
        int endIndex = singleLine && indexNewLine < length ? indexNewLine : length < buf.length() ? length : -1;
        if (endIndex != -1) {
            buf.setLength(endIndex);
            if (elipsize) {
                buf.append("...");
            }
        }
        return buf.toString();
    }


    public static String capitalize(String string) {
        return string.substring(0, 1).toUpperCase(Locale.getDefault()) + string.substring(1,
                string.length()).toLowerCase(Locale.getDefault());
    }


    /**
     * Checks if a query conditions searches for category
     * @param sqlCondition query "where" condition
     * @return Category id
     */
    public static String checkIntentCategory(String sqlCondition) {
        String pattern = DbHelper.KEY_CATEGORY + "\\s*=\\s*([\\d]+)";
        Pattern p = Pattern.compile(pattern);
        Matcher matcher = p.matcher(sqlCondition);
        if (matcher.find() && matcher.group(1) != null) {
            return matcher.group(1).trim();
        }
        return null;
    }


    /**
     * Choosing which date must be shown depending on sorting criteria
     * @return String ith formatted date
     */
    public static String getDateText(Context mContext, Note note, int navigation) {
        String dateText;
        String sort_column;
        SharedPreferences prefs = mContext.getSharedPreferences(Constants.PREFS_NAME, Context.MODE_MULTI_PROCESS);

        // Reminder screen forces sorting
        if (Navigation.REMINDERS == navigation) {
            sort_column = DbHelper.KEY_REMINDER;
        } else {
            sort_column = prefs.getString(Constants.PREF_SORTING_COLUMN, "");
        }

        switch (sort_column) {
            case DbHelper.KEY_CREATION:
				dateText = mContext.getString(R.string.creation) + " " + DateHelper.getFormattedDate(note.getCreation
						(), prefs.getBoolean(Constants.PREF_PRETTIFIED_DATES, true));
				break;
            case DbHelper.KEY_REMINDER:
				String noteReminder = note.getAlarm();
				if (TextUtils.isEmpty(noteReminder)) {
					dateText = mContext.getString(R.string.no_reminder_set);
				} else {
					dateText = mContext.getString(R.string.alarm_set_on) + " " + DateHelper.getDateTimeShort(mContext,
							Long.parseLong(noteReminder));
				}
				break;
            default:
				dateText = mContext.getString(R.string.last_update) + " " + DateHelper.getFormattedDate(note
						.getLastModification(), prefs.getBoolean(Constants.PREF_PRETTIFIED_DATES, true));
				break;
        }
        return dateText;
    }


	/**
	 * Gets an alternative title if empty
	 * @param context
	 * @param note
	 * @param spanned
	 * @return
	 */
	public static String getAlternativeTitle(Context context, Note note, Spanned spanned) {
		if (spanned.length() > 0) {
			return spanned.toString();
		}
		return context.getString(R.string.note) + " " + context.getString(R.string.creation) + " " + DateHelper
				.getDateTimeShort(context, note.getCreation());
	}

}

public class TimePickerFragment extends DialogFragment {

    public static final String DEFAULT_TIME = "default_time";

    TextView timer_label;
    private Activity mActivity;
    private OnTimeSetListener mListener;
    private Long defaultTime = null;


    @Override
    public void onAttach(Activity activity) {
        super.onAttach(activity);
        mActivity = activity;
        if (getArguments().containsKey(DEFAULT_TIME)) {
            this.defaultTime = getArguments().getLong(DEFAULT_TIME);
        }

        try {
            mListener = (OnTimeSetListener) mActivity;
        } catch (ClassCastException e) {
            throw new ClassCastException(activity.toString()
                    + " must implement OnTimeSetListener");
        }
    }


    @Override
    public Dialog onCreateDialog(Bundle savedInstanceState) {

        Calendar cal = DateUtils.getCalendar(defaultTime);
        int hour = cal.get(Calendar.HOUR_OF_DAY);
        int minute = cal.get(Calendar.MINUTE);

        // Create a new instance of TimePickerDialog and return it
        boolean is24HourMode = DateUtils.is24HourMode(mActivity);
        TimePickerDialog tpd = new TimePickerDialog(mActivity, R.style.Theme_AppCompat_Dialog_NoBackgroundOrDim, mListener, hour, minute, is24HourMode);
        tpd.setTitle("");
        return tpd;
    }

}

