public class PermissionsHelper {


	public static void requestPermission(Activity activity, String permission, int rationaleDescription, View
			messageView, OnPermissionRequestedListener onPermissionRequestedListener) {

		if (ContextCompat.checkSelfPermission(activity, permission) != PackageManager.PERMISSION_GRANTED) {

			if (ActivityCompat.shouldShowRequestPermissionRationale(activity, permission)) {
				Snackbar.make(messageView, rationaleDescription, Snackbar.LENGTH_INDEFINITE)
						.setAction(R.string.ok, view -> {
							requestPermissionExecute(activity, permission, onPermissionRequestedListener, messageView);
						})
						.show();
			} else {
				requestPermissionExecute(activity, permission, onPermissionRequestedListener, messageView);
			}
		} else {
			onPermissionRequestedListener.onPermissionGranted();
		}
	}


	private static void requestPermissionExecute(Activity activity, String permission, OnPermissionRequestedListener
			onPermissionRequestedListener, View messageView) {
		RxPermissions.getInstance(activity)
				.request(permission)
				.subscribe(granted -> {
					if (granted) {
						onPermissionRequestedListener.onPermissionGranted();
					} else {
						String msg = activity.getString(R.string.permission_not_granted) + ": " + permission;
						Snackbar.make(messageView, msg, Snackbar.LENGTH_LONG).show();
					}
				});
	}
}

public class LanguageHelper {

	/**
	 * Updates default language with forced one
	 */
	@SuppressLint("ApplySharedPref")
	public static Context updateLanguage(Context ctx, String lang) {
		SharedPreferences prefs = ctx.getSharedPreferences(Constants.PREFS_NAME, MODE_MULTI_PROCESS);
		String language = prefs.getString(Constants.PREF_LANG, "");

		Locale locale = null;
		if (TextUtils.isEmpty(language) && lang == null) {
			locale = Locale.getDefault();
			prefs.edit().putString(Constants.PREF_LANG, locale.toString()).commit();
		} else if (lang != null) {
			locale = getLocale(lang);
			prefs.edit().putString(Constants.PREF_LANG, lang).commit();
		} else if (!TextUtils.isEmpty(language)) {
			locale = getLocale(language);
		}

		return setLocale(ctx, locale);
	}

	private static Context setLocale(Context context, Locale locale) {
		Configuration configuration = context.getResources().getConfiguration();
		if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.JELLY_BEAN_MR1) {
			configuration.setLocale(locale);
			context.createConfigurationContext(configuration);

		} else {
			configuration.locale = locale;
			context.getResources().updateConfiguration(configuration, context.getResources().getDisplayMetrics());
		}
		return context;
	}

	/**
	 * Checks country AND region
	 */
	public static Locale getLocale(String lang) {
		if (lang.contains("_")) {
			return new Locale(lang.split("_")[0], lang.split("_")[1]);
		} else {
			return new Locale(lang);
		}
	}

	@TargetApi(Build.VERSION_CODES.JELLY_BEAN_MR1)
	@NonNull
	public static String getLocalizedString(Context context, String desiredLocale, int resourceId) {
		Configuration conf = context.getResources().getConfiguration();
		conf = new Configuration(conf);
		conf.setLocale(getLocale(desiredLocale));
		Context localizedContext = context.createConfigurationContext(conf);
		return localizedContext.getResources().getString(resourceId);
	}

}

